function themestab(id){  
    if(id == 1){       
        $("#menu11").show();
        $("#menu12").hide();
        $("#menu13").hide();
        $("#CUSTOM_NEWS_FILTER .nav-tabs li").removeClass('active');
        $("#tagstab").parent('li').addClass('active');
         filterCall(1);
        return false;
    }else if(id == 2){  
        $("#menu11").hide();
        $("#menu12").show();
        $("#menu13").hide();        
        $("#CUSTOM_NEWS_FILTER .nav-tabs li").removeClass('active');
        $("#themestab").parent('li').addClass('active');
         filterCall(1);
        return false;
    }else if(id == 3){
        $("#menu11").hide();
        $("#menu12").hide();
        $("#menu13").show();
        $("#CUSTOM_NEWS_FILTER .nav-tabs li").removeClass('active');
        $("#countrytab").parent('li').addClass('active');
         filterCall(1);
        return false;
    }
}
